mapper.select:
select id,asset,tx_hash txHash,amount volume, pay_status status,created  ctime, modified ,pay_time payTime,user_id userId
FROM deposit_record


        user_id=#{userId}


        and asset=#{asset}




            and id <  #{queryId}
            ORDER BY id desc
            LIMIT 0,${pageSize}



            and id >= #{queryId}
            ORDER BY id asc
            LIMIT 0,${pageSize}


            ORDER BY id desc
            LIMIT 0,${pageSize}

select id,asset,tx_hash txHash,amount volume, pay_status status,created ctime,modified ,pay_time payTime,user_id userId,pay_finish_time depositTime
FROM deposit_record


        user_id=#{userId}


        and asset=#{asset}
