SELECT id transferId,asset,  amount qty,created,status,src_account_type  fromAccount,target_account_type toAccount FROM fund_transfer


            user_id=#{userId}


            and asset=#{asset}




                and id <  #{queryId}
                ORDER BY id desc
                LIMIT 0,${pageSize}


                and id >= #{queryId}
                ORDER BY id asc
                LIMIT 0,${pageSize}


                ORDER BY id desc
                LIMIT 0,${pageSize}

