mapper.select:
SELECT IFNULL(SUM(amount),0) from withdrawal_record
  WHERE 1=1

      and  user_id=#{userId}


      AND asset=#{asset}

  AND pay_status NOT IN (1,2);

select id,asset symbol,  amount, pay_status  status,created,account
FROM withdrawal_record


        user_id=#{userId}


        and asset=#{asset}




            and id >= #{queryId}
            ORDER BY id asc
            LIMIT 0,${pageSize}


            and id < #{queryId}
            ORDER BY id desc
            LIMIT 0,${pageSize}

select id,asset symbol,  amount, pay_status  status,created,account
FROM withdrawal_record


        user_id=#{userId}


        and asset=#{asset}


ORDER BY id desc
LIMIT 1
